from Crypto.Util.number import *
import random

def cocktail(p, q):
    # To make this drink becomes perfect, I'll will need 2 flavour
    def spicy(a, b):
        return "?", b # To spice thing up
    def sour(a, b):
        return a, "?" # Why did the lemon stop in the middle of the road? It ran out of juice and needed a moment to sourt things out!
   
    lisp = list(str(bin(p))[2:])
    lisq = list(str(bin(q))[2:])

    # Make-a-cocktail time!!!
    for i in range(p.bit_length()):
        dice = random.randint(1, 3)
        if dice == 1:   lisp[i], lisq[i] = spicy(lisp[i], lisq[i])
        elif dice == 2: lisp[i], lisq[i] = sour(lisp[i], lisq[i])
        else:           continue

    # I accidentally leak some ingredients out but you won't able to guess what they were >:)
    leak_p = "".join(lisp)
    leak_q = "".join(lisq)

    return leak_p, leak_q


flag = b"W1{xxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxxx}"
flag = bytes_to_long(flag)

p = getPrime(3)
q = getPrime(3)
leak_p, leak_q = cocktail(p, q)
n = p*q 
e = 0x10001
c = pow(flag, e, n)


print(f"{n = }")
print(p, q)
print(f"{c = }")
print(f"{leak_p = }")
print(f"{leak_q = }")


